<h1 id="å‰‘æŒ‡offer40-æœ€å°çš„kä¸ªæ•°" tabindex="-1"><a class="header-anchor" href="#å‰‘æŒ‡offer40-æœ€å°çš„kä¸ªæ•°" aria-hidden="true">#</a> å‰‘æŒ‡offer40ï¼šæœ€å°çš„Kä¸ªæ•°</h1>
<blockquote>
<p>é¢˜ç›®ï¼šè¾“å…¥nä¸ªæ•´æ•°ï¼Œæ‰¾å‡ºå…¶ä¸­æœ€å°çš„Kä¸ªæ•°ã€‚ä¾‹å¦‚è¾“å…¥4,5,1,6,2,7,3,8è¿™8ä¸ªæ•°å­—ï¼Œåˆ™æœ€å°çš„4ä¸ªæ•°å­—æ˜¯1,2,3,4ã€‚</p>
</blockquote>
<p>ç»´æŠ¤kä¸ªå…ƒç´ çš„æœ€å¤§å †ï¼Œå³ç”¨å®¹é‡ä¸ºkçš„æœ€å¤§å †å­˜å‚¨æœ€å…ˆéå†åˆ°çš„kä¸ªæ•°ï¼Œå¹¶å‡è®¾ä»–ä»¬æ˜¯æœ€å°çš„kä¸ªæ•°ã€‚ç»§ç»­éå†æ•°åˆ—ï¼Œæ¯æ¬¡éå†åˆ°ä¸€ä¸ªå…ƒç´ ï¼Œå°†å®ƒä¸å †é¡¶å…ƒç´ æ¯”è¾ƒï¼Œè‹¥å°äºå †é¡¶å…ƒç´ ï¼Œæ›´æ–°å †ï¼Œå¦åˆ™ï¼Œä¸æ›´æ–°å †ã€‚</p>
<p>c++:</p>
<div class="language-c++ line-numbers-mode" data-ext="c++"><pre v-pre class="language-c++"><code>class Solution {
public:
    vector&lt;int&gt; GetLeastNumbers_Solution(vector&lt;int&gt; input, int k) {
        vector&lt;int&gt; minVec;
        
        int size = input.size();
        if(size &lt; k || k &lt;= 0 || size &lt;= 0) return minVec;
        else if(size == k) return input;
        
        for(int i = 0; i &lt; k; i++) {
            minVec.push_back(input[i]);
        }
        
        // å»ºç«‹å¤§é¡¶å †
        build_heap(minVec);
        
        for(int i = k; i &lt; size; i++) {
            if(input[i] &lt; minVec[0]) {
                minVec[0] = input[i];
                heapify(minVec, 0);
            }
        }
        
        return minVec;
    }
private:
    void heapify(vector&lt;int&gt; &amp;tree, int i) {
        int size = tree.size();
        int last_index = size - 1;
        if(i &gt; (last_index - 1) / 2) return;
        
        int max = i, left_child = 2 * i + 1, right_child = 2 * i + 2;
        if(left_child &lt; size &amp;&amp; tree[left_child] &gt; tree[max]) {
            max = left_child;
        }
        if(right_child &lt; size &amp;&amp; tree[right_child] &gt; tree[max]) {
            max = right_child;
        }
        if(max != i) {
            swap(tree[i], tree[max]);
            heapify(tree, max);
        }
    }
    
    void build_heap(vector&lt;int&gt; &amp;tree) {
        int size = tree.size();
        int last_index = size - 1;
        for(int i = (last_index - 1) / 2; i &gt;= 0; i--) {
            heapify(tree, i);
        }
    }
