<div class="language-c++ line-numbers-mode" data-ext="c++"><pre v-pre class="language-c++"><code>class Solution {
public:
    vector&lt;int&gt; maxInWindows(const vector&lt;int&gt;&amp; num, unsigned int size)
    {
        vector&lt;int&gt; maxVec;
        if(num.size() &lt; size || size &lt;= 0) return maxVec;
        
        deque&lt;int&gt; maxIndex;
        for(int i = 0; i &lt; size; i++) {
            while(!maxIndex.empty() &amp;&amp; num[i] &gt;= num[maxIndex.back()]) {
                maxIndex.pop_back();
            }
            maxIndex.push_back(i);
        }
        maxVec.push_back(num[maxIndex.front()]);
        
        for(int i = size; i &lt; num.size(); i++) {
            if(maxIndex.front() &lt;= i - size) {
                maxIndex.pop_front();
            }
            while(!maxIndex.empty() &amp;&amp; num[i] &gt;= num[maxIndex.back()]) {
                maxIndex.pop_back();
            }
            maxIndex.push_back(i);
            maxVec.push_back(num[maxIndex.front()]);
        }
        return maxVec;
    }
};
