\begin{aligned}
&amp;min\ &amp;-H(P)&amp;=\sum_{i=1}^nP(x_i)\text{log}\ P(x_i)\\
&amp;s.t.\ &amp;\sum_{i=1}^nP(x_i)&amp;=\sum_{i=1}^n\tilde{P}(x_i)=1\\
&amp;\ &amp;\text{min}(x_i)&amp;=a\\
&amp;\ &amp;\text{max}(x_i)&amp;=b\\
\end{aligned}
