<p v-pre class='katex-block'><span class="katex-display"><span class="katex"><span class="katex-mathml"><math xmlns="http://www.w3.org/1998/Math/MathML" display="block"><semantics><mtable rowspacing="0.25em" columnalign="right left" columnspacing="0em"><mtr><mtd><mstyle scriptlevel="0" displaystyle="true"><mrow></mrow></mstyle></mtd><mtd><mstyle scriptlevel="0" displaystyle="true"><mrow><mrow></mrow><msub><mi>z</mi><mn>1</mn></msub><mo>=</mo><msub><mi>θ</mi><mn>11</mn></msub><msub><mi>v</mi><mn>1</mn></msub><mo>+</mo><msub><mi>θ</mi><mn>12</mn></msub><msub><mi>v</mi><mn>2</mn></msub></mrow></mstyle></mtd></mtr><mtr><mtd><mstyle scriptlevel="0" displaystyle="true"><mrow></mrow></mstyle></mtd><mtd><mstyle scriptlevel="0" displaystyle="true"><mrow><mrow></mrow><msub><mi>z</mi><mn>2</mn></msub><mo>=</mo><msub><mi>θ</mi><mn>21</mn></msub><msub><mi>v</mi><mn>1</mn></msub><mo>+</mo><msub><mi>θ</mi><mn>22</mn></msub><msub><mi>v</mi><mn>2</mn></msub></mrow></mstyle></mtd></mtr></mtable><annotation encoding="application/x-tex">
\begin{aligned}
